public class Ops {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =2;
		int j = 5;
		double r = 3.5;
		
		System.out.println(i+j); //forventet 7
		System.out.println(i-j); //forventet -3
		System.out.println(j-i); //forventet 3
		System.out.println(i*j); //forventet 10
		System.out.println(i/j); //forventet ?
		System.out.println(j/i); //forventet ?
		
		System.out.println(i*r); //forventet 7
		System.out.println(i/r); //forventet ?
		System.out.println(r/i); //forventet ?

	}

}
